function initSlider(){jQuery(".slider").each(function(e){var i=jQuery(this),t=jQuery(this).data("single");jQuery(this).find(".post").hover(function(){var e=i.parent().find(".slider-control");e.hasClass("active")||e.addClass("active")},function(){var e=i.parent().find(".slider-control");e.hasClass("active")&&e.removeClass("active")}),jQuery(this).owlCarousel({autoPlay:jQuery(this).data("autoplay"),slideSpeed:3e3,paginationSpeed:400,singleItem:t}),jQuery(this).siblings(".next").click(function(){i.trigger("owl.next")}),jQuery(this).siblings(".prev").click(function(){i.trigger("owl.prev")})}),jQuery(".slider-wrap").fadeIn()}$(document).ready(function(){initSlider()});